INSERT INTO `premier_news` (`id`, `type`, `date`, `author`, `heading`, `announce`, `text`, `publish`, `pic`) VALUES
(5, '', '1238771825', '', '', '<p>Пополнен раздел <a href=\"/company/ability/?phpMyAdmin=lLjWbDRhhXhabVuz0vu1s24msof&phpMyAdmin=0f2c05edd3e54c08ee7b5295599cf674\">возможностей</a> трафаретной печати, где собраны интересные образцы со всего мира.</p>', '', '1', ''),
(6, '', '1258728958', '', '', 'Готовим к&nbsp;производству 3 новые модели станков для трафаретной печати с улучшенными характеристиками.', '', '1', ''),
(4, '', '1238598199', '', '', '<p>Первое&nbsp;апреля стало Днем рождения нашего сайта. Открываемся несмотря на незавершенность некоторых разделов.&nbsp;Просим простить за временные неудобства. </p>', '', '1', ''),
(7, '', '1246955897', '', '', 'Вывод 4-ой новости для симметрии. Первое&nbsp;апреля стало Днем рождения нашего сайта.', '', '1', ''),
(8, '', '1263220849', '', '', 'Разработан ложемент к станку&nbsp;<a href=\"/equipment/cylindrical/1/?phpMyAdmin=0f2c05edd3e54c08ee7b5295599cf674\">Премьер&nbsp;Радиус</a>&nbsp;для печати на плоских круглых пепельницах. В разработке ложемент для печати на ручках вкруговую.<br />', '', '1', ''),
(9, '', '1263220793', '', '', 'Все <a href=\"/equipment/rotary/1/?phpMyAdmin=0f2c05edd3e54c08ee7b5295599cf674\">карусельные станки</a> комплектуются керамическими столами, с безупречно ровной и гладкой поверхностью, способные выдерживать многократные нагревы и перепады температуры, что позволяет получать отпечатки очень высокого качества.', '', '1', ''),
(10, '', '1263134547', '', '', 'Копировальная рама <a href=\"/equipment/prepress/1/?phpMyAdmin=0f2c05edd3e54c08ee7b5295599cf674\">Премьер Копи</a>, совмещенная с сушильным шкафом &laquo;2&nbsp;в 1&raquo;, модернизирована. Теперь возможно производить одновременную засветку&nbsp;одной рамы и сушку&nbsp;четырех рам. Это&nbsp;сокращает время при подготовке печатных форм для карусельных станков.', '', '1', '');

